package mypackage;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	// different types of datatypes
		// primitive datatypes
		  	// for whole numbers
		
		
		byte runs;
		runs= 123;
		System.out.println(runs);
		
		short salary;
		salary= 32567;
		System.out.print(salary);
		
		int turnover;
		turnover= 4567859;
		System.out.println(turnover);
		
		long population;
		population= 2345789109999099890l;
		System.out.println(population);
		
		
		
		// for decimal number, fractional number
		
		float overs;
		overs= 45.78f;
		System.out.println(overs);
		
		double weight;            // for more precision
		weight= 5.789695d;
		System.out.println(weight);
		
		double weight1;  // even it takes whole number by giving '.0' results
		weight1= 56.000009d;
		System.out.println(weight1);
		
	
		// for single character, word  (char)
		  // lets store gender
		
		char gender;
		gender= 'M';
		System.out.println(" my gender is "+gender);
		
			// lets store vitamins
		
		char vitamin;
		vitamin= 'c';
		System.out.println(" This is happening due to deficiency of vitamin "+vitamin);
		
		
		
		
		// for true ,false statement (boolean)
			// lets store my result
				// pass--> true, fail-->false
		
		boolean result;
		result= true;
		System.out.println(" my result of previous year examination is "+result);
		
			// on--> true, off-->false
		
		boolean fan;
		fan= true;
		System.out.println(" the switch of my fan is "+fan);
		
		
		
		// NON-PRIMITIVE DATATYPES
			// for collection ,group of characters
		
		String name;
		name= "Velocity";
		System.out.println(" the name of  my coaching institute is "+name+" corporate training centre");
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
